import { geoOrthographic, geoPath, geoGraticule, append, count } from "d3";
import React, { useState, useCallback, useEffect, useRef } from "react";
import $, { data } from "jquery";

export const Marks = ({ data: { land, geometry } }) => {
  const width = 500;
  const height = 500;
  // const intialMousePosition = { x: width / 2, y: height / 2 };
  const rotate = [0, -23.5];
  const velocity = [0.015, -0];
  const [dt, setDT] = useState(Date.now());



  const projection = geoOrthographic()
    .scale(300).clipAngle(90);;
  const path = geoPath(projection);
  const graticule = geoGraticule();

  const time = Date.now();

  const [rotation, setRotation] = useState({rotation:0});
  const savedCallback = useRef();


  const [counter, setCounter] = useState(0);

  function useInterval(callback, delay) {

    useEffect(() => {
      savedCallback.current = callback;
    }, [callback]);

    // Set up the interval.
    useEffect(() => {
      let id = setInterval(() => {
        savedCallback.current();
      }, delay);
      return () => clearInterval(id);
    }, [delay]);

  };








  useInterval(() => {
    setCounter(counter + 0.2);
  }, 1);

  console.log("counter", counter);


//   useEffect(() => {
//     const interval = setInterval(() => {

//       setDT( Date.now()-0.2);
//       // handleMouseMove();
//     }, 200);


//      return () => clearInterval(interval); 



// // This represents the unmount function, in which you need to clear your interval to prevent memory leaks.
//   }, []);

  return (
    <g
      className="marks"
      //  onMouseDown={handleMouseDown}
      //  onMouseMove={handleMouseMove}
      // onMouseUp={handleMouseUp}
    >
      {projection
    .rotate([ velocity[1]-counter, rotate[1] + velocity[1] * dt])}
      <path className="sphere" d={path({ type: "Sphere" })} />
      <path
        className="graticule"
        d={path(graticule())}
        style={{ display: "none" }}
      />

      {land.features.map((feature, i) => (
        <path className="feature" d={path(feature)} key={i} />
      ))}

      {geometry.features.map((feature, i) => (
        <g>
          <path
            className="interiors"
            d={path(feature)}
            key={feature.properties.name}
            id={feature.properties.name}
          >
            {/* <div     cx={path.centroid(feature)[0]} cy={path.centroid(feature)[1]}   fontSize="36">{feature.properties.name}</div> */}
          </path>

          {path.centroid(feature)[0] ?? (
            <circle
              className="interiors"
              key={i}
              cx={path.centroid(feature)[0]}
              cy={path.centroid(feature)[1]}
              r="2"
              fill="red"
              fontSize="36"
            ></circle>
          )}
        </g>
      ))}
    </g>
  );
};
