import React from "react";
import * as d3 from "d3";
import { feature, mesh } from 'topojson-client';
import { ReactDOM } from "react";

export class Globe extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      rotation: 0
    }
  }

  render() {
    let projection = d3.geoOrthographic()
      .fitSize([this.props.size, this.props.size], this.props.geoJson)
      .rotate([this.state.rotation])

    let geoGenerator = d3.geoPath()
      .projection(projection)

    let pathString = geoGenerator(this.props.geoJson)

    window.requestAnimationFrame(() => {
      this.setState({
        rotation: this.state.rotation + 0.2
      })
    })

    return <svg width={this.props.size} height={this.props.size} >
      <path d={pathString} />
    </svg>
  }
}

d3.json('https://unpkg.com/world-atlas@2.0.2/countries-50m.json', (err, json) => {
  let geoJson =  feature(json, json.objects.ne_110m_admin_0_countries) 

  ReactDOM.render(
    <Globe geoJson={geoJson} size={400} />,

  )
})
