import { geoOrthographic, geoPath, geoGraticule, timer} from "d3";
import React, { useState, useMemo, useEffect, useRef } from "react";
import { json } from 'd3';
import { feature, mesh } from 'topojson-client';


export const Globe = () => {
     const savedCallback = useRef();
    const [rotation, setRotation] = useState(0);
    const [data, setData] = useState(null);
    const url = "https://unpkg.com/world-atlas@2.0.2/countries-50m.json";



    const projection = geoOrthographic().rotate([rotation, -23])

    const geoGenerator = geoPath()
    .projection(projection)

    const pathString = geoGenerator(data)


    function useInterval(callback, delay) {

    useEffect(() => {
      savedCallback.current = callback;
    }, [callback]);

    // Set up the interval.
    useEffect(() => {
      let id = setInterval(() => {
        savedCallback.current();
      }, delay);
      return () => clearInterval(id);
    }, [delay]);

  };





  useEffect(() => {




     json(url).then(topojsonData => {
        let geoJson = feature(topojsonData, topojsonData.objects.land) 


       setData(geoJson
      )
    });


  }, []);






  useInterval(() => {
    setRotation(rotation + 0.2);
    console.log("interval", rotation)
  }, 200);


  return (

    <svg width={400} height={400} >
    <path d={pathString} />
  </svg>
  );
};
