import * as d3 from "d3";
import { geoOrthographic, geoPath, scaleLinear, geoGraticule } from "d3";

import React, { useState, useEffect } from "react";

export const Globe = ({ data: { land, geometry } }) => {
  useEffect(() => {
    renderD3Globe();
  }, []);

  const renderD3Globe = () => {
    const width = 400,
      height = 400;

    const projection = geoOrthographic()
      .scale(200)
      .translate([width / 2, height / 2])
      .clipAngle(90);

    const path = geoPath(projection);

    const graticule = geoGraticule();

    const λ = scaleLinear().domain([0, width]).range([-180, 180]);

    const φ = scaleLinear().domain([0, height]).range([90, -90]);



    const svg = d3
      .select("#globe")
      .append("svg")
      .attr("width", width)
      .attr("height", height);

//adds a gradient for styling use
      const defs = svg.append("defs");

      const gradient = defs.append("linearGradient")
      .attr("id", 'svgGradient')
      .attr("x1", "0%")
      .attr("x2", "100%")
      .attr("y1", "0%")
      .attr("y2", "100%");

      gradient.append("stop")
      .attr("class", "start")
      .attr("offset", "0%")
      .attr('stop-color', "#07f2a0")
      .attr('stop-opacity', 1);

      gradient.append("stop")
      .attr("class", "end")
      .attr("offset", "100%")
      .attr('stop-color',  "#323162")
      .attr("stop-opacity", 1);

      //end of gradient

    svg.append("circle").attr("class", "sphere").style("fill", "none");

    svg
      .append("path")
      .datum(land)
      .attr("class", "land")
      .attr("d", path)
      .style("fill", "url(#svgGradient)");

    svg.append("path").attr("class", "graticule").attr("d", path(graticule()));

    svg
      .append("path")
      .datum(geometry)
      .attr("class", "interiors")
      .attr("d", path(geometry));

      svg
      .append("g")
      .datum(geometry)
      .attr("class", "interiors")
      .attr("d", path(geometry));

      // const circles = svg.selectAll("circle");
      // sv.append("circle").enter()
      // .attr("class", "bubble")

      // .attr("r", 10)
      // .style("fill", "white")

      // .data(geometry.features)
      // .attr("cx", function (d){ return path.centroid(d)[0]})

      // .attr("cy", function (d){ return path.centroid(d)[1]})


      //   circles.exit().remove();

      const markers = svg.selectAll('circle')
      .data(geometry.features);

        markers.enter()
        .data(geometry.features)
  .append("circle")
  .merge(markers)

        .style("stroke", "gray")
        .style("fill", "black")
        .attr("r", 2)
        .attr("d", path(geometry))
        // .attr("cx", function (d){ return path.centroid(d)[0]})
        // .attr("cy", function (d){ return path.centroid(d)[1]});
        .attr('cx', d => projection([d.centroid(d)[0]]))
        .attr('cx', d => projection([d.centroid(d)[1]]))
            const scrollSpeed = 50;

    let current = 0;
    function bgscroll() {
      current += 2;
      projection.rotate([-λ(current), -24]);
      svg.selectAll("path").attr("d", path);
    }





    setInterval(bgscroll, scrollSpeed);
  };

  return (
    <div className="marks">
      <div id="globe"></div>
    </div>
  );
};
